		@font-face {
			font-family: 'Open Sans';
			src: url('./assets/fonts/OpenSans-Regular.ttf');
			font-style: normal;
			font-weight: 400;
		}

		@font-face {
			font-family: 'Open Sans';
			src: url('./assets/fonts/OpenSans-Bold.ttf');
			font-style: normal;
			font-weight: 700;
		}

		@font-face {
			font-family: 'Roboto';
			src: url('./assets/fonts/Roboto-Regular.ttf');
			font-style: normal;
			font-weight: 400;
		}

		@font-face {
			font-family: 'Oranienbaum';
			src: url('./assets/fonts/Oranienbaum-Regular.ttf');
			font-style: normal;
			font-weight: 400;
		}
		
		amp-story-page {
			background-color: #fff;
		}

		body {
			font-family: 'Open Sans';
		}

		.logo-layer {
			justify-items: flex-end;
			padding: 3.125vh 30px;
		}

		.page-title {
			font-family: 'Oranienbaum';
			font-weight: 400;
			font-size: 40px;
			line-height: 1;
			color: #3F4440;
			margin: 0 0 1.875vh
		}

		.content-layer {
			padding: 10.3vh 30px 0;
		}

		.page-text {			
			font-size: 14px;
			line-height: 1.43;
			color: #3F4440;
			margin: 0 0 3.125vh;
		}		

		.slider {
			position: relative;
			left: -30px;
			width: calc(100% + 60px);
		}		

		.slider__navigation {
			display: flex;
			align-items: center;
			justify-content: space-between;			
			padding: 0 30px;
			margin-top: 14px;
		}
		
		.slider__controls {
			display: flex;
			align-items: center;
			gap: 18px;
			height: 37px;
			position: relative;
		}

		.slider__control {
			display: flex;
			align-items: center;
			justify-content: center;
			width: 37px;
			height: 37px;
			border-radius: 50%;
			border: 1px solid #D3D7DA;
			position: static;			
			margin: 0;
		}

		.slider__control::after {
			content: '';
			width: 8px;
			height: 14px;
			background: no-repeat center;
			background-size: contain;
		}

		.swiper-button-prev::after {
			background-image: url('./assets/nav-arrow-left.svg');
		}

		.swiper-button-next::after {
			background-image: url('./assets/nav-arrow-right.svg');
		}

		.swiper-pagination {
			position: static;
			display: flex;
			align-items: flex-start;
			width: fit-content;
			color: #D3D7DA;
			font-size: 32px;
			line-height: 1;
			font-family: 'Oranienbaum';
		}

		.swiper-pagination-current {
			color: #4D9D34;
		}

		.swiper-pagination-total {
			font-size: 20px;
		}

		.cta-layer {			
			padding: 0 30px 32px;
		}

		.btn {
			display: flex;
			align-items: center;
			justify-content: center;
			border: none;
			gap: 19px;
			height: 62px;
			border-radius: 31px;
			color: white;
			font-size: 16px;
			background-color: #4D9D34;
			cursor: pointer;
		}

		.swiper-slide {
			height: 36.7vh;
			width: 100%;
			object-fit: cover;
			object-position: bottom center;
		}

		.parks-list {
			display: flex;
			flex-direction: column;
			gap: 6px;
		}

		.parks-list__item {
			display: flex;
			align-items: center;
			gap: 7px;
			font-weight: 700;
			font-size: 13px;
			line-height: 1.54;			
			color: #3F4440;
		}	
		
		.page-1-3-bg {
			padding: 60px 0 0;
		}	
		
		.page-1-3-content {
			padding: 0 calc(8.33vw + 3px);
		}

		.page-1-4-bg {
			padding: 70px 30px 0;
		}

		.page-1-4-gradient {
			padding: 0 0 180px;
		}

		.page-1-4-layer {
			padding: 0 30px 100px;
		}

		.page-1-4-content {
			background-color: #fff;
			margin: auto 0 0;
		}

		.form {
			display: flex;
			flex-direction: column;
			gap: 20px;
			margin-bottom: 12px;
		}

		.input-block {
			display: flex;
			flex-direction: column;
			gap: 10px;
		}

		.input {			
			width: 100%;
			height: 48px;
			border-radius: 24px;
			border: none;
			background: #F8F8F8;
			box-shadow: inset 0px 2px 5px rgba(0, 0, 0, 0.03);
			font-size: 18px;
			padding: 0 20px;
		}

		.input-label {
			font-family: 'Open Sans';
			font-weight: 600;
			font-size: 13px;			
			color: #3F4440;
		}

		.subscription-text {
			font-size: 12px;
			line-height: 1.33;
			letter-spacing: -0.02em;
			color: #8E9FAC;
		}

		.form-socials {
			display: flex;
			align-items: center;
			gap: 9px;
		}

		.form-socials-text {
			font-weight: 400;
			font-size: 15px;
			line-height: 1.47;	
			color: #8E9FAC;
			margin-left: 14px;
		}	

		.page-subtitle {
			font-weight: 400;
			font-size: 14px;
			line-height: 1.57;
			color: #3F4440;
		}

		.title-layer {
			padding: 15vh 20px 0;
		}

		@media (max-width: 375px) {
			.cta-layer {			
				padding-bottom: 20px;
			}

			.swiper-slide {
				height: 30vh;				
			}

			.parks-list__item img {
				width: 32px;
				height: auto;
			}			

			.page-1-4-gradient {
				padding: 0 0 220px;
			}
		}

		@media (orientation: landscape) {
			.page-1-3-content {
				padding: 0 calc(8.33% + 3px);
			}

			.page-1-4-gradient {
				padding: 0 0 230px;
			}

			.page-text {			
				font-size: 2vh;				
			}	

			.parks-list__item {
				font-size: 2vh;
			}	
		}
	</style>
